package stackconcept;

public class S13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int k=0;
		for(int i=1;i<=4;i++)
		{
			k=4-i;
			for(int j=1;j<=k;j++)
			{
				System.out.print(" ");
				
			}
			for (int m=1;m<=i;m++)
			{
				System.out.print("*");
			}System.out.println();
		}

	}

}
